using System;
using System.IO;
using System.Reflection;
using System.Text.RegularExpressions;

namespace AdventOfCode
{
    class Program
    {
        static void Main(string[] args)
        {
            Execute(
                Day3_Squares_With_Three_Sides.Day3.Part2,
                File.ReadAllText("Resources/Day3 Input.txt"));

            Execute(
                Day3_Squares_With_Three_Sides.Day3.Part1,
                File.ReadAllText("Resources/Day3 Input.txt"));

            Execute(
                Day2_Bathroom_Security.Day2.Part2,
                "\r\n" +
                "\r\n" +
                "\r\n" +
                "\r\n" +
                "");

            Execute(
                Day2_Bathroom_Security.Day2.Part1,
                "\r\n" +
                "\r\n" +
                "\r\n" +
                "\r\n" +
                "");
        }

        static void Execute<T>(Func<string, T> action, string input)
        {
            var method = action.GetMethodInfo();
            var result = action(input);
            var day = method.DeclaringType.Name;
            var part = Regex.Replace(method.Name, "\\w+(\\d+)", "$1");

            Console.WriteLine($"{day}.{part} = {result}");
        }
    }
}
